export function getAppointmentsForDay(state, day) {
  const filteredDays = state.days.filter(val => val.name === day);
  const arr = [];

  filteredDays.map(day => {
    day.appointments.map(appointment => {
      arr.push(state.appointments[appointment]);
    });
  });

  return arr;
}
//Get interviewers for a specific day
export function getInterviewersForDay(state, day) {
  const {days, interviewers  } = state;
  if (!days.length) return [];
  const selectedinterviewers = days.filter(sday => sday.name === day)
  if (!selectedinterviewers.length) return [];
  return selectedinterviewers[0].interviewers.map(id => interviewers[id]);
}

export function getInterview(state, interview) {
  const obj = {}

  for(let key in state.interviewers) {
    if (interview === null) return null;

    if (state.interviewers[key].id === interview.interviewer) {
      obj['student'] = interview.student;
      obj['interviewer'] = state.interviewers[key];
    }
  }

  return obj;
}
